graphql.query:
query HealthCheck {
  healthCheck {
    status
    timestamp
    databaseConnected
    version
    uptimeSeconds
  }
}

query Ping {
  ping
}

mutation CreateInventoryItem($input: CreateInventoryItemInput!) {
  createInventoryItem(input: $input) {
    success
    message
    item {
      id
      name
      category
      unit
      currentStock
      reorderPoint
      costPerUnit
    }
  }
}

mutation UpdateInventoryItem($input: UpdateInventoryItemInput!) {
  updateInventoryItem(input: $input) {
    success
    message
    item {
      id
      name
      category
      reorderPoint
      costPerUnit
      shelfLifeDays
      storageRequirements
    }
  }
}

mutation DeleteInventoryItem($input: DeleteInventoryItemInput!) {
  deleteInventoryItem(input: $input) {
    success
    message
  }
}

mutation CreateSupplier($input: CreateSupplierInput!) {
  createSupplier(input: $input) {
    success
    message
    supplier {
      id
      name
      contactEmail
      contactPhone
      streetAddress
      city
      state
      zipCode
      country
      latitude
      longitude
      notes
    }
  }
}

